void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float PI=3.1415926;
    float strength=5000.0;

    float t=mod(iTime*0.2,1.0);
    float d=1.0-sin((uv.x-t+0.5)*PI);
    d*=strength;
    vec3 oCol=vec3(0.23,0.98,0.1);
    vec3 orign=texture(iChannel0,uv).rgb;
    float gray = 0.2125 *orign.r + 0.7154 *orign.g + 0.0721 *orign.b;

    vec3 col0=mix(vec3(gray),orign,step(uv.x,t));
    vec3 col=oCol/d+col0;
    fragColor = vec4(col,1.0);
}
