#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define S(v)  smoothstep( 1.5,0., v )
#define Sa(v) S(abs(v))
void mainImage( out vec4 c, in vec2 o ){
    c=vec4(0);
    o-=iResolution.xy/2.0;o*=rot(iTime);
    o/=min(iResolution.x,iResolution.y)/360.0;
    vec2 o2=o*rot(radians(60.0));
    vec2 o3=-o*rot(radians(-60.0));
    float x=o2.y,y=o3.y,z=o.y;
    vec3 d1 = vec3(x-50.,y-50.,z/50.);
    vec3 d2 = vec3(x+50.,y+50.,z/50.); 
    d1.xy*=rot(iTime);d2.xy*=rot(iTime);
    d1=abs(d1);d2=abs(d2);
    c+=Sa(max(d1.x,max(d1.y,d1.z))-50.)*S(length(d1)-50.*sqrt(2.));
    c+=Sa(max(d2.x,max(d2.y,d2.z))-50.)*S(length(d2)-50.*sqrt(2.));
    c+=Sa(z+100.*sin(iTime))*S(abs(o.x+57.*cos(iTime))-57.);
    c+=Sa(z-100.*cos(iTime))*S(abs(o.x+57.*sin(iTime))-57.);
    c+=Sa(z-100.*sin(iTime))*S(abs(o.x-57.*cos(iTime))-57.);
    c+=Sa(z+100.*cos(iTime))*S(abs(o.x-57.*sin(iTime))-57.);
}
