const vec3 CAMERA_POS =  vec3(-5,2,-6);
const float INF = 1e10;
const float EPS = 1e-4;
const float CYL_RADIUS = 2.7;


const vec3 LIGHT1_POS = vec3(-3,0.5,2);
const float LIGHT1_RADIUS = 0.4;
const vec3 LIGHT1_COLOR = vec3(1,1,0);


const vec3 LIGHT2_POS = vec3(3,1,2);
const float LIGHT2_RADIUS = 0.3;
const vec3 LIGHT2_COLOR = vec3(0,1,1);


const vec3 LIGHT3_POS = vec3(3, 0.8, -2);
const float LIGHT3_RADIUS = 0.8;
const vec3 LIGHT3_COLOR = vec3(1,0,1);


const float GLASS_N=1.5;
const float AIR_N = 1.0;
const float WATER_N = 1.33;


struct Triangle {
    vec3 color;
    vec3 normal;
    float specular;
    vec3 pos[3];
};

const uint TRIANGLES_COUNT = 6u;
Triangle TRIANGLES[TRIANGLES_COUNT];

vec3 randDir;


void createtriangle()
{
    TRIANGLES[0].pos = vec3[3](vec3(0.0, 1.5, 0.0) , vec3(-0.75, -1.0, -1.25), vec3(-1.25, -1.0, 0.75));
    TRIANGLES[0].color = vec3(1, 0, 0);
    TRIANGLES[0].normal = normalize(vec3(80, -34, 20));

    TRIANGLES[1].pos = vec3[3](vec3(0.0, 1.5, 0.0) , vec3(0.75, -1.0, 1.25), vec3(-1.25, -1.0, 0.75));
    TRIANGLES[1].color = vec3(1, 0, 0);
    TRIANGLES[1].normal = normalize(vec3(20, -34, -80));

    TRIANGLES[2].pos = vec3[3](vec3(0.0, 1.5, 0.0) , vec3(0.75, -1.0, 1.25), vec3(1.25, -1.0, -0.75));
    TRIANGLES[2].color = vec3(1, 0, 0);
    TRIANGLES[2].normal = normalize(vec3(80, 34, 20));

    TRIANGLES[3].pos = vec3[3](vec3(0.0, 1.5, 0.0) , vec3(-0.75, -1.0, -1.25), vec3(1.25, -1.0, -0.75));
    TRIANGLES[3].color = vec3(1, 0, 0);
