    TRIANGLES[4].pos = vec3[3](vec3(0.75, -1.0, 1.25) , vec3(-0.75, -1.0, -1.25), vec3(1.25, -1.0, -0.75));
    TRIANGLES[4].color = vec3(1, 0, 0);
