}


float tracePlane(vec3 pos, vec3 dir, out vec3 normal) {
    float t = (-1.2 - pos.y) / dir.y;
    if (t <= 0.0)
        return INF;
    vec3 WorldPos = t * dir + pos;
    if (dot(WorldPos.xz, WorldPos.xz) >= 100.0)
        return INF;
    normal = vec3(0, 1, 0);
    return t;
}


float traceSphere(vec3 pos, vec3 dir, float r, out vec3 normal, float noize_val) {
    float noize = noize_val * 0.08 * sin(15.0 * (pos.x + dir.x)) * sin(15.0 * (pos.y + dir.y)) * sin(15.0 * (pos.z + dir.z));
    float b = dot(pos,dir);
    float D = b*b - dot(pos,pos) + (r+noize) * (r+noize);
    if (D < 0.0) 
        return INF;
    float t = -b - sqrt(D);
    if (t > 0.0) {
        normal = normalize(pos + t * dir);
        return t;
    }
    t = -b + sqrt(D);
    if (t < 0.0)
        return INF;
    normal = normalize(pos + t * dir);
    return t;
}


float traceCylinder(vec3 pos, vec3 dir, out vec3 normal){
    float t = (-1.0 - pos.y)/dir.y;
    if(t<=0.0)
        return INF;
    vec3 worldPos = t*dir + pos;
    if(dot(worldPos.xz,worldPos.xz)<CYL_RADIUS){
        normal = vec3(0,1,0);
        return t;
    }
    float a = dot(dir.xz,dir.xz);
    float b = dot(pos.xz,dir.xz);
    float D = b*b - a*(dot(pos.xz,pos.xz) - CYL_RADIUS);
    if(D<0.0)
        return INF;
    t=(-b-sqrt(D))/a;
    if(t>0.0){
        worldPos = t*dir + pos;
        if(worldPos.y<=-1.0){
            normal = normalize(vec3(worldPos.x,0,worldPos.z));
            return t;
        }
    }
    t=(-b+sqrt(D))/a;
    if(t<0.0)
        return INF;
    worldPos = t*dir + pos;
    if(worldPos.y<=-1.0){
        normal = normalize(vec3(worldPos.x,0,worldPos.z));
        return t;
    }
    return INF;
}

float traceTriangle(Triangle t, vec3 origin, vec3 dir) {
    vec3 edges[3] = vec3[3](
        t.pos[1] - t.pos[0],
        t.pos[2] - t.pos[1],
        t.pos[0] - t.pos[2]
    );
    vec3 norm = normalize(cross(edges[0],edges[1]));
    float normalViewAngle = dot(dir, t.normal);
    if (abs(normalViewAngle) < EPS)
        return INF;
    float d = dot(t.pos[0] - origin, t.normal) / normalViewAngle;
    if (d < 0.0)
        return INF;
    vec3 hitPos = dir * d + origin;
    float square = length(cross(edges[0], edges[1]));
    vec3 toHitVecs[3] = vec3[3](
        hitPos - t.pos[0],
        hitPos - t.pos[1],
        hitPos - t.pos[2]
    );
    float testSq[3] = float[3](
        length(cross(toHitVecs[0], edges[0])),
        length(cross(toHitVecs[1], edges[1])),
        length(cross(toHitVecs[2], edges[2]))
    );
    if (abs(square - testSq[0] - testSq[1] - testSq[2]) < EPS)
        return d;
    return INF;
}

bool isOccluded(vec3 pos, vec3 target){
    vec3 dir = target - pos;
    float dist = length(dir);
    dir /= dist;
    float t = traceTriangle(TRIANGLES[0], pos, dir);
    for(int i = 1; i < 6; i++){
       float trg = traceTriangle(TRIANGLES[1], pos, dir);
        if (trg < t)
            t = trg;
    }
    if (t < dist)
        return true;
    vec3 cylNorm;
    float cylT = traceCylinder(pos, dir, cylNorm);
    return (cylT < dist);
    return false;
} 


vec3 computeLight(vec3 pos, vec3 color, vec3 normal){

    vec3 toLight[3] = vec3[3] (
        LIGHT1_POS - pos,
        LIGHT2_POS - pos,
        LIGHT3_POS - pos
    );

    float distSq[3] = float[3] (
        dot(toLight[0], toLight[0]),
        dot(toLight[1], toLight[1]),
        dot(toLight[2], toLight[2])
    );

    float att[3] = float[3] (
        isOccluded(pos, LIGHT1_POS + randDir * LIGHT1_RADIUS) ? 0.0 : 50.0f / distSq[0],
        isOccluded(pos, LIGHT2_POS+ randDir * LIGHT2_RADIUS) ? 0.0 : 30.0f / distSq[1],
        isOccluded(pos, LIGHT3_POS + randDir * LIGHT3_RADIUS) ? 0.0 : 40.0f / distSq[2]
    );

    return color * (max(0.0, dot(normal, normalize(toLight[0]))) * att[0] * LIGHT1_COLOR
      + max(0.0, dot(normal, normalize(toLight[1]))) * att[1] * LIGHT2_COLOR
      + max(0.0, dot(normal, normalize(toLight[2]))) * att[2] * LIGHT3_COLOR)
      + texture(iChannel1, normal).rgb * 0.3;
}

vec3 refraction(vec3 v, vec3 normal, float n1, float n2){
    if (dot(v, normal) < 0.0)
        normal = -normal;
    float cosA = dot(v, normal);
    float sinA = sqrt(1.0 - cosA * cosA);
    vec3 tang = normalize( v - cosA * normal);
    float sinB = sinA / n2 * n1;
    if (sinB > 1.0){
        return reflect(v, normal);
    }
    float cosB = sqrt(1.0 - sinB * sinB);
    return sinB * tang + cosB * normal;
}

float pow2(float x){
    return x * x;
}

float rand(float frame){
    return fract(sin(dot(vec3(frame), vec3(12.9898, 78.233, 45.5432))) * 43758.5453);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec3 randVals = vec3(rand(float(iFrame)), rand(float(iFrame + 5)), rand(float(iFrame + 15)));
    randDir = normalize(randVals - 0.5);
    vec2 uv = (fragCoord - iResolution.xy*0.5 + (randVals.xy - 0.5) * 2.0) / iResolution.x;
    vec3 front = normalize(-CAMERA_POS);
    vec3 up = vec3 (0, 1, 0);
    vec3 right = normalize(cross(front, up));
    up = normalize(cross(right, front));
    vec3 ViewVec = normalize(front + right * uv.x + up * uv.y);

    const int EMISSION = 0;
    const int DIFFUSE  = 1;
    const int REFLECTION = 2;
    const int REFRACTION = 3;

    vec3 curPos = CAMERA_POS;
    vec3 curDir = ViewVec;
    float n1 = AIR_N;
    float n2 = GLASS_N;
    float GLASS_R = pow2(AIR_N - GLASS_N) / pow2(AIR_N + GLASS_N);
    float WATER_R = pow2(WATER_N - GLASS_N) / pow2(WATER_N + GLASS_N);
    const float DIAMOND_N = 2.5;
    float DIAMOND_R = pow2(DIAMOND_N - GLASS_N) / pow2(DIAMOND_N + GLASS_N);
    vec3 colorMult = vec3(1, 1, 1);
    float a = 1.0;
    bool isFullResolation = false;

    createtriangle();

    for (int i = 0; i < 20; i++){
        float t = INF;
        int materialType;
        vec3 color;
        vec3 normal;
        float nEnter = AIR_N;
        vec3 planeNorm;
        float planeT = tracePlane(curPos, curDir, planeNorm);
        if (planeT < t){
            t = planeT;
            materialType = DIFFUSE;
            vec3 WorldPos = t * curDir + curPos;
            color = texture(iChannel0, WorldPos.xz * 0.1).rgb;
            normal = planeNorm;
            if (randVals.y < 0.5){
                materialType = REFLECTION;
            }
        }
        vec3 l1Norm;
        float light1T = traceSphere(curPos - LIGHT1_POS, curDir, LIGHT1_RADIUS, l1Norm, 0.0);
        if (light1T < t){
            t = light1T;
            materialType = EMISSION;
            color = LIGHT1_COLOR;
            normal = l1Norm;
        }
        vec3 l2Norm;
        float light2T = traceSphere(curPos - LIGHT2_POS, curDir, LIGHT2_RADIUS, l2Norm, 0.0);
        if (light2T < t){
            t = light2T;
            materialType = EMISSION;
            color = LIGHT2_COLOR;
            normal = l2Norm;
        }
        vec3 l3Norm;
        float light3T = traceSphere(curPos - LIGHT3_POS, curDir, LIGHT3_RADIUS, l3Norm, 0.0);
        if (light3T < t){
            t = light3T;
            materialType = EMISSION;
            color = LIGHT3_COLOR;
            normal = l3Norm;
        }
        float AMBIENT_N = pow2(AIR_N - GLASS_N) / pow2(AIR_N * GLASS_N);
        for(int i = 0; i < 6; i++){
            float trg = traceTriangle(TRIANGLES[i],curPos, curDir);
            if (trg < t){
                t = trg;
                if (randVals.x < GLASS_R){
                    materialType = REFLECTION;
                }else if (randVals.x < AMBIENT_N){
                    materialType = DIFFUSE;
                } else {
                    materialType = REFRACTION;
                    if (dot(curDir, normal) > 0.0){
                        n1 = AIR_N;
                    }else{
                        n2 = GLASS_N;
                    }
                }
                normal = TRIANGLES[i].normal;
                color = vec3(0.5, 0.5, 1.0);
            }
        }
        vec3 sphNorm;
        float sph2T = traceSphere(curPos + vec3(0.0, 0.4, 0.0), curDir, 0.4, sphNorm, 1.8);
        if (sph2T < t){
            t = sph2T;
            normal = sphNorm;
            vec3 worldPos = t * curDir + curPos;
            color = texture(iChannel3, worldPos).rgb + vec3(0, 0.4, 0.5);
            a = texture(iChannel3, worldPos).a;
            if (randVals.x * 0.005 < WATER_R){
                materialType = EMISSION;
            } 
            else{
                materialType = REFRACTION;
                if (dot(curDir, normal) > 0.0){
                    nEnter = GLASS_N;
                } 
                else{
                    nEnter = WATER_N;
                }
            }
        }
        vec3 cylNorm;
        float cylT = traceCylinder(curPos, curDir, cylNorm);
        if (cylT < t){
            t = cylT;
            materialType = DIFFUSE;
            vec3 WorldPos = t * curDir + curPos;
            color = texture(iChannel2, WorldPos.xz * WorldPos.y).rgb;
            normal = cylNorm;
        }
        if (t != INF) {
            vec3 worldPos = t * curDir + curPos;
            fragColor = texture(iChannel0, worldPos.xz);
            if (materialType == EMISSION) {
                fragColor.rgb = color * colorMult;
                break;
            } else if (materialType == DIFFUSE) {
                fragColor.rgb = computeLight(worldPos, color, normal) * colorMult;
                break;
            } else if (materialType == REFLECTION) {
                curDir = reflect(curDir, normal);
                curPos = worldPos + curDir * 1e-5;
            } else if (materialType == REFRACTION) {
                curDir = refraction(curDir, normal, n1, n2);
                curPos = worldPos + curDir * 1e-5;
            }    
        } else {
            fragColor.rgb = texture(iChannel1, curDir).rgb * colorMult;
            break;
        }
    }
}

