renderpass.code:
vec4 firstColor = vec4(1.0,1.0,1.0,1.0);
vec4 secondColor = vec4(0.25,0.5,1,1.0);
vec4 thirdColor = vec4(0.0,0.0,0.5,1.0);
vec4 fourthColor = vec4(0.0,0.0,0.0,0.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy;

    float h = 0.333;
    vec4 col1 = mix(mix(firstColor, secondColor, xy.x/h), mix(secondColor, thirdColor, (xy.x - h)/(1.0 - h*2.0)), step(h, xy.x));  
    vec4 col2 = mix(mix(secondColor, thirdColor, (xy.x - h)/(1.0 - h*2.0)), mix(thirdColor, fourthColor, (xy.x - h*2.0)/(1.0-h*2.0)), step(h*2.0, xy.x));
    vec4 col = mix(col1,col2,step(h*2.0,xy.x));
    fragColor = col;
}
