renderpass.code:
float PI = 3.14,
      PHI = 1.61,
     R0 = .5,
     R1 = .45,
     dR = .9,
     n = 7.;

void mainImage( out vec4 O, in vec2 u )
{
    vec2 Res = iResolution.xy, 
        U = ( 2. * u - Res ) / Res.y;   
    O-=O;
    int m = int(mod(iTime/4. * n, n) + 1.);

    float a = (PI/2.) * iTime + PI/2.;
    float r = distance(U, R0 * vec2(cos(a), sin(a)));
    vec3 star = vec3(exp((-r + R1 * dR)/.05));

    for(int i = 0; i < m; i++) {
        a = float(i) * PI/(float(n)/2.);
        r = distance(U, R0 * vec2(-sin(a), cos(a)));
        O += vec4(min(exp((-r + R1 * dR)/.02), 1.)) * 0.3;
    }

    O+= vec4(star, 1.);
}
