void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec3 txt1 = texture(iChannel0, uv).rgb;
    fragColor = vec4(txt1 , 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-0.5*iResolution.xy)/iResolution.y;
    vec2 UV = fragCoord.xy/iResolution.xy * 1.002 - 0.001;

    float t = iTime * 10.0;
    uv.x += sin(t) * 0.4;
    uv.y += cos(t) * 0.4;
    uv += sin(t * 0.81) * 0.2;

    vec3 txt = texture(iChannel0, UV).rgb;
    vec3 ch2 = texture(iChannel1, UV).rgb;
    vec3 col = ch2 * 0.01 / max(0.01,length(uv)) + txt*0.99;

    fragColor = vec4( col * vec3(0.97,0.99,0.0) , 1.0);
}
