float HexDist(vec2 p){
    p=abs(p);
    float c=dot(p,vec2(1.,1.73));
    c=max(c,p.x);
    return c;
}
vec4 HexCoords(vec2 uv){
    vec2 rect =vec2(1.0,1.73);
    vec2 helfR =rect*0.5;

    vec2 a=mod(uv,rect)-helfR;
    vec2 b=mod(uv-helfR,rect)-helfR;
    float flag=step(length(a)-length(b),0.0);
    vec2 gv=a*flag+b*(1.0-flag);
    vec2 id=uv-gv;
    return vec4(gv,id);
}
