float foobar (vec2 uv, float r, float a)
{
     float rr = length(uv) * 2.0 / r;
     a = cos(uv.y / uv.x) + a / r;
     float f = smoothstep(-0.4, 1.0, cos(a * r * 10.0)) * 0.2 / r + 0.3;
     return 1.0 - smoothstep(f, f + 0.02,rr)-step(length(uv), 0.02);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
	vec2 pos = vec2(0.6) - uv;

    pos.x *= iResolution.x / iResolution.y + 10.0 / iTime;
    pos.y -= -0.5 + cos(0.1 + sin(0.8 * iTime));

    vec3 color = vec3(0.0);
	float f = foobar(pos, 0.8, iTime)+ foobar(pos + vec2(-0.7, 0.1), 1.5, -cos(iTime) - 0.3) + foobar(pos + vec2(0.45, 0.45), 1.2, -sin(iTime));
    color = vec3(f);
    fragColor = vec4(color,1.0) * vec4(0.99 * cos(0.8 * iTime), sin(0.4 * iTime), cos(0.6 * iTime), 1.0);
}
