void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = texture(iChannel0, uv).rgb;


    if(uv.x > 0.4 && uv.x < 0.6 && uv.y > 0.4 && uv.y < 0.7){
        //col = vec3(sin(uv.x), sin(uv.y), 0.5);
        //col = vec3(sin(uv.x*200.0 + cos(uv.y*200.0 + sin(iTime * 8.0) * 4.0 ) + iTime * 8.0) * 2.0);
        col = vec3(texture(iChannel0, uv).r * mod(iTime / 4.0, 1.0), (mod(15.0, (uv.y * uv.x) * (4.0 * ((2.0 + uv.y) * uv.x))) * 1.0) ,.5);

    }
    else{
        col = vec3(texture(iChannel0, uv).r * mod(iTime / 4.0, 1.0), (sin(uv.x*200.0 + cos(uv.y*200.0 + sin(iTime * 8.0) * 16.0 ) + iTime * 8.0) * 4.0), 0.7);
    }



    // Output to screen
    fragColor = vec4(col, 1.0);
}
