void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = texture(iChannel0,uv).rgb;

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define Key_R 82.
#define Key_C 67.
#define Key_G 71.
#define Key_E 69.
bool isPressed(float keyCode) {
        keyCode = (keyCode + 0.5) / 256.0;
        vec2 uv = vec2(keyCode, 0.25);
        float key = texture(iChannel2, uv).r;

        return key > 0.0;
}
bool isToggled(float keyCode) {
        keyCode = (keyCode + 0.5) / 256.0;
        vec2 uv = vec2(keyCode, 0.75);
        float key = texture(iChannel2, uv).r;

        return key > 0.0;
}
bool isKeyDown(float keyCode){
    return(texelFetch( iChannel2, ivec2(keyCode, 0),0 ).x==1.);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    int sizeOfCell=10;
    vec3 col;

    col=texture(iChannel0,uv).rgb;


    if(isToggled(Key_E)){
       col=texture(iChannel3,uv).rgb;
    }
    if(isKeyDown(Key_R)||iTime<=0.5f){
         col=texture(iChannel1,uv).rgb;
    }
    if(isKeyDown(Key_C)){
        col=vec3(0,0,0);
    }

    if(!isToggled(Key_G)&&((int(fragCoord.x)%sizeOfCell==0&&fragCoord.x>1.)||(int(fragCoord.y)%sizeOfCell==0&&fragCoord.y>1.))){
        col=Color(uv,iTime);
    }
    else if(((int(fragCoord.x)%sizeOfCell==0&&fragCoord.x>1.)||(int(fragCoord.y)%sizeOfCell==0&&fragCoord.y>1.))){
        col=vec3(0,0,0);
    }
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    int sizeOfCell=10;
    vec3 col=texture(iChannel0,uv).rgb;
    vec2 coord=vec2(int(fragCoord.x)/sizeOfCell+1,int(fragCoord.y)/sizeOfCell+1);
    int cnt=0;

    for(int i=0;i<=8;i++){
        int posx,posy;
        if(i%3==0){
            posx=-1;
        }
        else if((i+1)%3==0){
            posx=1;
        }
        else{
            posx=0;
        }
        if(i>=6){
            posy=1;
        }
        else if(i<=2){
            posy=-1;
        }
        else{
            posy=0;
        }

        vec2 newCoord=coord+vec2(posx,posy);
        if(i!=4&&(texture(iChannel0,(newCoord*float(sizeOfCell)-vec2(1,1))/iResolution.xy).rgb!=vec3(0,0,0))&&newCoord.x>=0.&&newCoord.y>=0.){
            cnt+=1;
            //col=normalize(vec3(newCoord*float(sizeOfCell)/iResolution.xy,1.));
        }

    }
    if(cnt==3&&col==vec3(0,0,0)){
        col=Color(uv,iTime);
    }
    else if(cnt>=2&&cnt<=3&&col!=vec3(0,0,0)){
        col=Color(uv,iTime);
    }
    else if(cnt<2||cnt>3){
        col=vec3(0,0,0);
    }



    fragColor = vec4(col,1.0);
}

/*
Control
Toggle Key E - edit field (LMB to create/delete cell)
Toggle Key G - enable/disable grid
Press Key C - clear field
Press Key R - randomize field




*/





vec3 Color( vec2 uv , float iTime )
{
    return 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
}
float rand(vec2 co){ return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453); }
float rand(float co) { return fract(sin(co*(91.3458)) * 47453.5453); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    int sizeOfCell=10; 
    vec3 col=vec3(0,0,0);
    vec2 coord=vec2(int(fragCoord.x)/sizeOfCell,int(fragCoord.y)/sizeOfCell);
    if(rand(coord*iTime*float(iFrame))>=0.5f){
        col=Color(uv,iTime);
    }
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    int sizeOfCell=10; 
    vec2 coord=vec2(int(fragCoord.x)/sizeOfCell,int(fragCoord.y)/sizeOfCell);
    vec3 col=texture(iChannel0,uv).rgb;
    if(vec2(int(iMouse.x)/sizeOfCell,int(iMouse.y)/sizeOfCell)==coord&&(iMouse.w>0.)){
        if(col==vec3(0,0,0)){
            col=Color(uv,iTime);
        }
        else{
            col=vec3(0,0,0);
        }
    }

    fragColor = vec4(col,1.0);
}
