const float Soft = 0.001;
const float Threshold = 0.3;

vec4 threshold(in vec2 fragCoord) {
    float f = Soft/2.0;
	float a = Threshold - f;
	float b = Threshold + f;

	vec4 tx = texture(iChannel0, fragCoord.xy/iResolution.xy);
	float l = (tx.x + tx.y + tx.z) / 3.0;

	float v = smoothstep(a, b, l);

	vec4 resultImage = vec4(v);
    return resultImage;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec4 baseColor = threshold(fragCoord);

    if (baseColor.rgb == vec3(1.0)) { // white color
        baseColor.rgb = vec3(sin(iTime*2.5),cos(iTime*2.5),sin(cos(iTime*2.5)));
    }

    fragColor = baseColor;
}
