void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec3 Color = vec3(0.1, 0.3, 0.9);
	float col = -0.2;
    float col2 = -0.2;

	vec2 a = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
	for(float i=0.0;i<50.0;i++)
	{

	  float si = (sin(iTime + i * 0.05))/0.3;
	  float co = acos(cos(iTime + i * 0.5))*0.2;
      float co2 = ceil(cos(iTime + i * 0.5))*0.2;

	  col += 0.01 / abs(length(a+vec2(si*0.5,co))- 0.1);
      col2 += 0.01 /abs(length(a+vec2(si*1.15,co2-0.5))- 0.1);
	}
	fragColor = vec4(vec3(Color * col+vec3(0.5,0.0,0.0)*col2), 5.0);
}
