void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy,
         uv = fragCoord/R.xy,
         st = (fragCoord-.5*R.xy)/R.y;
    float c = (.5+.5*abs(pow(sin(iTime - length(st) * 10.), 19.))) * .05;
    st = mix(uv, uv * c, c);
    fragColor = texture(iChannel0, st) * 1. - c * 6. * (.8 - uv.y);
}
