// Color-Space Conversion RGB to HSV written 2015 by Jakob Thomsen
// https://en.wikipedia.org/wiki/HSL_and_HSV

// RGB转HSV的函数
// 色调（H）、饱和度（S）和明度（V）。
// 色调H     用角度度量，取值范围为0°～360°，从红色开始按逆时针方向计算，红色为0°，绿色为120°,蓝色为240°。它们的补色是：黄色为60°，青色为180°,紫色为300°；
// 饱和度S    光谱色的白光成分为0，饱和度达到最高。通常取值范围为0%～100%，值越大，颜色越饱和
// 明度V    明度表示颜色明亮的程度，对于光源色，明度值与发光体的光亮度有关；对于物体色，此值和物体的透射比或反射比有关。通常取值范围为0%（黑）到100%（白）。
vec3 RGBtoHSV(vec3 rgb)
{
    // Hue: red = 0/6, yellow = 1/6, green = 2/6,
    //      cyan = 3/6, blue = 4/6, magenta = 5/6
    vec3 hsv;
    float cmax = max(rgb.r, max(rgb.g, rgb.b));
    float cmin = min(rgb.r, min(rgb.g, rgb.b));

    hsv.z = cmax; // value

    float chroma = cmax - cmin;
    //if(chroma != 0.0)
    {
        hsv.y = chroma / cmax; // saturation

        //if(cmax == rgb.r)
        if(rgb.r > rgb.g && rgb.r > rgb.b)
        {
            hsv.x = (0.0 + (rgb.g - rgb.b) / chroma) / 6.0; // hue
        }
        //else if(cmax == rgb.m_Green)
        else if(rgb.g > rgb.b)
        {
            hsv.x = (2.0 + (rgb.b - rgb.r) / chroma) / 6.0; // hue
        }
        else
        {
            hsv.x = (4.0 + (rgb.r - rgb.g) / chroma) / 6.0; // hue
        }

        // Make sure hue is in range [0..1]
        hsv.x = fract(hsv.x);
    }
    //else
    //{
    //    hsv.x = 0.0; // rnd();
    //}
    return hsv;
}

// HSV转RGB的函数
vec3 HSVtoRGB(in vec3 c) // thanks to iq for this much more compact version!
{
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // 取纹理坐标
	vec2 uv = fragCoord.xy / iResolution.xy;
    // 取图片纹理像素值
    vec3 rgb = texture(iChannel0, uv).rgb;
    // 把纹理像素值转HSV
    vec3 hsv = RGBtoHSV(rgb);
    // 取随机数
    float v = .5 - .5 * cos(.1 * iTime * 2. * 3.1415926);

    // 左边：变化饱和度  右变：变化色调 left: de-saturate, right: rotate hue
    // 左边饱和度：决定是否彩色。saturation
    // 右边色调：决定颜色的基调  HUE
    // hsv = mix(hsv * vec3(1.,v,1.), fract(hsv + vec3(v,0.,0.)), step(0.5, uv.x)); 
    hsv = mix(hsv * vec3(1.,1.,v), fract(hsv + vec3(v,0.,0.)), step(0.5, uv.x)); 


    // 把hsv转为rgb 作为屏幕输出源
    rgb = HSVtoRGB(hsv);

    // 颜色输出
	fragColor = vec4(rgb, 1.0);
}
