float segment( in vec2 p, vec2 a, vec2 b ) {
	vec2 pa = p - a;
	vec2 ba = b - a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0., 1. );
	return length( pa - ba*h );
}void mainImage( out vec4 c, in vec2 o ) {
    o-=iResolution.xy/2.0;
    float m=min(iResolution.x,iResolution.y);
    o/=m/360.0;
    o.y-=64.0;
    c=vec4(0);
    vec2 A=vec2(cos(iTime)*100.0,sin(iTime)*50.0);
    vec2 B=vec2(cos(iTime+6.283/6.)*100.0,sin(iTime+6.283/6.)*50.0);
    vec2 C=vec2(cos(iTime+6.283/6.*2.0)*100.0,sin(iTime+6.283/6.*2.0)*50.0);
    vec2 D=vec2(cos(iTime+6.283/6.*3.0)*100.0,sin(iTime+6.283/6.*3.0)*50.0);
    vec2 E=vec2(cos(iTime+6.283/6.*4.0)*100.0,sin(iTime+6.283/6.*4.0)*50.0);
    vec2 F=vec2(cos(iTime+6.283/6.*5.0)*100.0,sin(iTime+6.283/6.*5.0)*50.0);
    vec2 A1=A*1.25;vec2 B1=B*1.25;vec2 C1=C*1.25;vec2 D1=D*1.25;vec2 E1=E*1.25;vec2 F1=F*1.25;
    A1.y-=32.0;B1.y-=32.0;C1.y-=32.0;D1.y-=32.0;E1.y-=32.0;F1.y-=32.0;
    vec2 G3=vec2(0,-164.0);
    if (segment(o,A,B)<=1.0)c=vec4(1.0);
    if (segment(o,B,C)<=1.0)c=vec4(1.0);
    if (segment(o,C,D)<=1.0)c=vec4(1.0);
    if (segment(o,D,E)<=1.0)c=vec4(1.0);
    if (segment(o,E,F)<=1.0)c=vec4(1.0);
    if (segment(o,F,A)<=1.0)c=vec4(1.0);
    if (segment(o,A1,B1)<=1.0)c=vec4(1.0);
    if (segment(o,B1,C1)<=1.0)c=vec4(1.0);
    if (segment(o,C1,D1)<=1.0)c=vec4(1.0);
    if (segment(o,D1,E1)<=1.0)c=vec4(1.0);
    if (segment(o,E1,F1)<=1.0)c=vec4(1.0);
    if (segment(o,F1,A1)<=1.0)c=vec4(1.0);
    if (segment(o,A,A1)<=1.0)c=vec4(1.0);
    if (segment(o,B,B1)<=1.0)c=vec4(1.0);
    if (segment(o,C,C1)<=1.0)c=vec4(1.0);
    if (segment(o,D,D1)<=1.0)c=vec4(1.0);
    if (segment(o,E,E1)<=1.0)c=vec4(1.0);
    if (segment(o,F,F1)<=1.0)c=vec4(1.0);
    if (segment(o,A1,G3)<=1.0)c=vec4(1.0);
    if (segment(o,B1,G3)<=1.0)c=vec4(1.0);
    if (segment(o,C1,G3)<=1.0)c=vec4(1.0);
    if (segment(o,D1,G3)<=1.0)c=vec4(1.0);
    if (segment(o,E1,G3)<=1.0)c=vec4(1.0);
    if (segment(o,F1,G3)<=1.0)c=vec4(1.0);
    c*=34.0/vec4(abs(o.x),abs(o.y),min(abs(o.y+o.x),1.0),abs(o.y*o.x));
}
