void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord + iMouse.xy)/iResolution.xy - vec2(0.5, 0.5);
    //line1:
    float x = uv.x*3.5 + iTime+sin(iTime*2.0)*0.03;
    float y = (uv.y-0.5) * (4.0 + sin(iTime*0.7) * 1.1 + sin(uv.y));   
    float dis = abs(y - sin(x)) + (cos(x * 2.0) + 1.0) * 0.001;
    dis = smoothstep(0.01, 0.0, dis);
    dis *= (sin(sin(iTime + x*2.0)*0.3 + x*1.7) + 1.1) * 2.0; 
    float a = dis * 0.8;
    //line2:
    x = uv.x*2.6 + iTime+sin(iTime*2.5)*0.04;
    y = (uv.y-0.5) * (5.0 + sin(iTime*0.7) * 1.3 + sin(uv.y)) + 0.3;  
    dis = abs(y - sin(x)) + (cos(x * 2.0) + 1.0) * 0.001;
    dis = smoothstep(0.05, 0.0, dis);
    dis *= (sin(sin(iTime + x*2.0)*0.3 + x*1.7) + 1.1) * 2.0;  
    a += dis * 0.2;
    //line2:
    x = uv.x*3.6 + iTime*0.95+sin(iTime*2.5)*0.04;
    y = (uv.y-0.5) * (5.0 + sin(iTime*0.7) * 1.3 + sin(uv.y)) + 0.3;  
    dis = abs(y - sin(x));
    dis = smoothstep(0.08, 0.0, dis);
    dis *= (sin(sin(iTime + x*2.0)*0.3 + x*1.7) + 1.2) * 2.0;  
    a += dis * 0.1;
    //background:
    vec3 lineColor = vec3(0.85, 0.1, 0.1);
    vec3 bgColor = vec3(0.15, 0.25, 0.4);
    bgColor += vec3(1.0, 1.0, 1.0) * (1.0 + uv.x - uv.y * 0.8) * 0.3;
    bgColor.r += (sin((iTime*1.5 + uv.x*2.0)/4.0) + 1.0) * 0.25;
    bgColor.b += (sin((iTime*1.2 + uv.y*5.0)/3.0) + 1.0) * 0.5;

    vec3 finalColor = mix(bgColor, lineColor, a);

    // Output to screen
    fragColor = vec4(finalColor, 1.0);
}
