float circle(vec2 uv, vec2 pos, float r, float blur) {
    uv -= pos;
    float d = length(uv);
    float c = smoothstep(r, r-blur, d);

    return c;
}

float band(float t, float a, float b, float w) {
    return smoothstep(a-w,a+w,t) * smoothstep(b+w,b-w,t);
}

float rect(vec2 uv, vec2 BL, vec2 TR, float blur) {
    float b1 = band(uv.x, BL.x, TR.x, blur);
    float b2 = band(uv.y, BL.y, TR.y, blur);
    return b1 * b2;
}

float smiley(vec2 uv, vec2 pos, float size) {
    uv -= pos; 
    uv /= size;
    float c = circle(uv, vec2(0.0), .4, 0.05);
    c -= circle(uv, vec2(-.13, .2), .07, 0.01);    
    c -= circle(uv, vec2(.13, .2), .07, 0.01);

    float mouth = circle(uv, vec2(0.), .3, .02);
    mouth -= circle(uv, vec2(0., .1), .3, .02);

    c -= clamp(mouth,0.,1.);

    return c;
}

float invlerp(float a, float b, float t) {
    return (t-a) / (b-a);
}

float lerp(float a, float b, float t) {
    return (t * (b-a)) + a;
}

float remap(float a, float b, float c, float d, float t) {
    return lerp(c, d, invlerp(a, b, t));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    float dist = (cos(iTime*3.)*4.) / 10.;
    float smile = smiley(uv, vec2(dist, .0), 1.);
    float smile2 = smiley(uv, vec2(-dist, .0), 1.);
    vec3 col = vec3(1., 0., 1.);
    vec3 col2 = vec3(1., 1., 0.);
    vec3 main = ((col * smile) + (col2 * smile2));

    float x = uv.x;
    float y = uv.y;
    //float m = (x-.6)*(x+.6);
    //y+=m*m*-2.;
    float m = sin(x*10.+iTime*10.)/10.;
    y += m;
    float blur = remap(-.6, .6, .01, .25, x);
    blur = pow(blur, 2.);
    vec3 main2 = vec3(rect(vec2(x, y), vec2(-.6,-.2),vec2(.6,.2),blur));
    fragColor = vec4(main2* main ,1.);
}
