void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;
	vec3 col = vec3(0.5 * (sin(iTime + uv.x) + 0.5), 0.5 * (cos(iTime + uv.y) + 0.5),1.000);
    col.r *= (uv.x > 0.05 && uv.x < 0.95 && uv.y > 0.05 && uv.y < 0.95)?1.0:0.0;
    fragColor = vec4(col, 0.5 * (sin(iTime + uv.x * 4.0 + uv.y * 4.0)) + 0.5);
}
