// Gridbender.glsl     2021-08-23

// Fork of "meshify" by FabriceNeyret2. 
//   https://shadertoy.com/view/XdcGzM

#define L 10.  // interline distance
#define A  4.  // amplification factor
#define P  8.  // thickness

void mainImage( out vec4 o,  vec2 uv )
{
  uv /= L;
  vec2 p = floor(uv+.5);

  #define T(x,y) texture(iChannel0,L*vec2(x,y)/iResolution.xy).g

  #define M(c,T) o += 0.2* pow(.5+.5*cos( 6.28*(uv-p).c + A*(2.*T-1.) ),P)

  o = vec4(0) + T( p.x, p.y); 
  M( y, T( uv.x, p.y ) );   // modulates y offset
  M( x, T( p.x, uv.y ) );   // modulates x offset
}
