void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    int idx = int(texelFetch(iChannel1, ivec2(fragCoord.x, fragCoord.y / 3.0 + 1.0), 0).x);
    fragColor = texelFetch(iChannel0, ivec2(idx, 0), 0);

    //fragColor = texelFetch(iChannel0, ivec2(fragCoord.xy), 0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 yellow = vec3(1, 0.8, 0);
    vec3 red = vec3(1, 0, 0);
    vec3 black = vec3(0, 0, 0);

    // Output to screen
    if (uv.x < 0.5)
        fragColor.rgb = mix(black, red, uv.x * 2.0);
    else
        fragColor.rgb = mix(red, yellow, uv.x * 2.0 - 1.0);

    //fragColor.rgb = floor(fragColor.rgb * 10.0) / 10.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (fragCoord.y == 0.5)
    {
        fragColor.x = floor(
            texelFetch(iChannel0, ivec2(fragCoord.x, iFrame % 1024), 0).x > 0.1
             ? 799.0 : 0.0);
    }
    else
    {
        fragColor.x = floor(
            (texelFetch(iChannel1, ivec2(fragCoord.x - 1.0, fragCoord.y - 1.0), 0).x
            + texelFetch(iChannel1, ivec2(fragCoord.x + 1.0, fragCoord.y - 1.0), 0).x
            + texelFetch(iChannel1, ivec2(fragCoord.x, fragCoord.y - 1.0), 0).x
            + texelFetch(iChannel1, ivec2(fragCoord.x, fragCoord.y - 2.0), 0).x
            ) * 0.25 - 15.0
        );
    }
}
