void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = texture(iChannel0, uv);
}

vec3 col1 = vec3(1.0);
vec3 col2 = vec3(1.0,1.0,0.0);
vec3 col3 = vec3(0.8,0.0,0.0);
vec3 col4 = vec3(0.2,0.0,0.8);
vec3 col5 = vec3(0.0,0.0,0.0);

vec3 grad(float t)
{
    if (t > .8)
    {
        t = (t-.8) * 5.0;
        return col1*t + col2*(1.0-t);
    }
    if (t > .6)
    {
        t = (t-.6) * 5.0;
        return col2*t + col3*(1.0-t);
    }
    if (t > .2)
    {
        t = (t-.2) * 2.5;
        return col3*t + col4*(1.0-t);
    }
    t = t * 5.0;
    return col4*t + col5*(1.0-t);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    if (fragCoord.x/iResolution.x*768.0 >= 767.0)
    {
        float fft = texelFetch(
            iChannel0,
            ivec2(
                512.0 * fragCoord.y / iResolution.y,
                0),
            0).x;
        fragColor = vec4(grad(fft),1.0);
    }
    else
        fragColor = texelFetch(iChannel1,ivec2(fragCoord+vec2(1,0)),0);
}
