#define PI 3.141592653

float bR = 3.;
float sR = 1.;


vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	return mat2(c, -s, s, c) * v;
}

vec3 circle(vec2 uv, vec2 pos, float r, float angle) {
    vec2 b = rotate(vec2(pos - uv) / r / 2., angle + PI/4.-0.1);

    vec3 c = texture(iChannel0, b).rgb;
    return c * (1.0 - smoothstep(r - 0.02, r + 0.02, distance(uv, pos)));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = (fragCoord - .5*iResolution.xy) / min(iResolution.x, iResolution.y);
    uv *= 11.0;
    if (mod(iTime, PI*4.)>PI*2.) {
        uv = rotate(uv, iTime);
    }

    vec3 col = vec3(0.);

    col += circle(uv, vec2(0.0), bR, PI);
    col += circle(
        uv,
        vec2(sin(iTime), cos(iTime)) * (bR+sR),
        sR,
        -iTime * (bR/sR+1.)
    );

    fragColor = vec4(col,1.0);
}
