vec3 HSV(float a){
return 
    abs(fract(a-vec3(0.,1.,2.)/3.0)
    *6.0-
    3.0)*(2.71);
}

void mainImage( out vec4 o,  vec2 i )
{
    i /= iResolution.xy;

float A = .001, B = .1; //THRESHOLD
float COLOR = iTime;//SHIFT

vec4 co = texture(iChannel0, i);

o= vec4(vec3(step(B,co.y))+vec3(HSV(dot(0.2,i.y-COLOR*2.))),1.0)*co;
}
