    float circle(vec2 uv, vec2 position, float r, float blur)
    {
        /*
        Creates circle with specified center position, radius and blur
        */

        uv -= position;
         uv.x *= iResolution.x / iResolution.y;
        float  d = length(uv);
        float   color = smoothstep(r, r-blur, d);
          return color;
    }


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float coeff = 0.01;



// Printing circles

    // Head
     float head = circle(uv, vec2(0.3, 0.8), 0.12, coeff);
     float head_ear1 = circle(uv, vec2(0.31, 0.92), 0.05, coeff);
     float head_ear2 = circle(uv, vec2(0.37, 0.81), 0.06, coeff);
     float head_nose = circle(uv, vec2(0.23, 0.81), 0.032, coeff);
     float head_inner_eye = circle(uv, vec2(0.313, 0.8), 0.035, coeff);
     float head_outer_eye = circle(uv, vec2(0.305, 0.803), 0.05, coeff);

     float head_mouth1 = circle(uv, vec2(0.24, 0.75), 0.014, coeff);
     float head_mouth2 = circle(uv, vec2(0.248, 0.753), 0.014, coeff);
     float head_mouth3 = circle(uv, vec2(0.245, 0.738), 0.014, coeff);

    // Neck
     float neck1 = circle(uv, vec2(0.32, 0.67), 0.055, coeff);
     float neck2 = circle(uv, vec2(0.33, 0.61), 0.055, coeff);
     float neck3 = circle(uv, vec2(0.342, 0.54), 0.055, coeff);
     float neck4 = circle(uv, vec2(0.354, 0.48), 0.05, coeff);

    // Body
     float body1 = circle(uv, vec2(0.386, 0.42), 0.075, coeff);
     float body2 = circle(uv, vec2(0.45, 0.43), 0.085, coeff);
     float body3 = circle(uv, vec2(0.52, 0.45), 0.075, coeff);

    // Tail
     float tail1 = circle(uv, vec2(0.548, 0.53), 0.04, coeff);
     float tail2 = circle(uv, vec2(0.56, 0.577), 0.036, coeff);
     float tail3 = circle(uv, vec2(0.567, 0.63), 0.04, coeff);
     float tail4 = circle(uv, vec2(0.578, 0.71), 0.07, coeff);

    // Leg 1
     float leg1_1 = circle(uv, vec2(0.355, 0.35), 0.042, coeff);
     float leg1_2 = circle(uv, vec2(0.335, 0.295), 0.042, coeff);
     float leg1_3 = circle(uv, vec2(0.317, 0.24), 0.043, coeff);
     float leg1_4 = circle(uv, vec2(0.3, 0.1855), 0.044, coeff);
     float leg1_5 = circle(uv, vec2(0.285, 0.135), 0.045, coeff);
     float leg1_6 = circle(uv, vec2(0.27, 0.07), 0.05, coeff);

    // Leg 2
     float leg2_1 = circle(uv, vec2(0.39, 0.34), 0.042, coeff);
     float leg2_2 = circle(uv, vec2(0.392, 0.28), 0.042, coeff);
     float leg2_3 = circle(uv, vec2(0.4, 0.22), 0.043, coeff);
     float leg2_4 = circle(uv, vec2(0.41, 0.16), 0.044, coeff);
     float leg2_5 = circle(uv, vec2(0.43, 0.11), 0.045, coeff);
     float leg2_6 = circle(uv, vec2(0.46, 0.065), 0.05, coeff);

    // Leg 3
     float leg3_1 = circle(uv, vec2(0.565, 0.4), 0.042, coeff);
     float leg3_2 = circle(uv, vec2(0.585, 0.35), 0.042, coeff);
     float leg3_3 = circle(uv, vec2(0.595, 0.29), 0.043, coeff);
     float leg3_4 = circle(uv, vec2(0.6, 0.23), 0.044, coeff);
     float leg3_5 = circle(uv, vec2(0.605, 0.17), 0.045, coeff);
     float leg3_6 = circle(uv, vec2(0.605, 0.1), 0.05, coeff);



// Painting circle with certain color

    // Head
     vec3 color_head = vec3(1.0, 0.7, 0.0) * head;
     vec3 color_head_ear1 = vec3(0.0, 0.0, 1.0) * head_ear1;
     vec3 color_head_ear2 = vec3(1.0, 0.3, 0.3) * head_ear2;
     vec3 color_head_nose = vec3(0.8, 0.27, 0.1) * head_nose;
     vec3 color_head_inner_eye = vec3(1.0, 1.0, 1.0) * head_inner_eye;
     vec3 color_head_outer_eye = vec3(0.8, 0.8, 1.0) * head_outer_eye;

     vec3 color_head_mouth1 = vec3(0.8, 0.27, 0.1) * head_mouth1;
     vec3 color_head_mouth2 = vec3(0.8, 0.27, 0.1) * head_mouth2;
     vec3 color_head_mouth3 = vec3(0.8, 0.27, 0.1) * head_mouth3;

    // Neck
     vec3 color_neck1 = vec3(0.0, 0.0, 1.0) * neck1;
     vec3 color_neck2 = vec3(0.0, 1.0, 0.0) * neck2;
     vec3 color_neck3 = vec3(0.0, 0.0, 1.0) * neck3;
     vec3 color_neck4 = vec3(1.0, 0.3, 0.3) * neck4;

    // Body
     vec3 color_body1 = vec3(0.0, 0.65, 1.0) * body1;
     vec3 color_body2 = vec3(1.0, 0.2, 0.0) * body2;
     vec3 color_body3 = vec3(1.0, 0.6, 0.0) * body3;

    // Tail
     vec3 color_tail1 = vec3(0.5, 0.0, 1.0) * tail1;
     vec3 color_tail2 = vec3(1.0, 0.3, 0.3) * tail2;
     vec3 color_tail3 = vec3(1.0, 0.4, 0.0) * tail3;
     vec3 color_tail4 = vec3(0.0, 0.65, 1.0) * tail4;

    // Leg 1
     vec3 color_leg1_1 = vec3(1.0, 0.1, 0.0) * leg1_1;
     vec3 color_leg1_2 = vec3(1.0, 0.4, 0.0) * leg1_2;
     vec3 color_leg1_3 = vec3(1.0, 0.6, 0.0) * leg1_3;
     vec3 color_leg1_4 = vec3(1.0, 0.4, 0.0) * leg1_4;
     vec3 color_leg1_5 = vec3(1.0, 0.3, 0.3) * leg1_5;
     vec3 color_leg1_6 = vec3(1.0, 0.7, 0.0) * leg1_6;

    // Leg 2
     vec3 color_leg2_1 = vec3(0.4, 0.0, 1.0) * leg2_1;
     vec3 color_leg2_2 = vec3(0.0, 0.55, 1.0) * leg2_2;
     vec3 color_leg2_3 = vec3(0.0, 0.65, 1.0) * leg2_3;
     vec3 color_leg2_4 = vec3(0.0, 0.7, 1.0) * leg2_4;
     vec3 color_leg2_5 = vec3(0.0, 1.0, 0.5) * leg2_5;
     vec3 color_leg2_6 = vec3(0.0, 1.0, 0.3) * leg2_6;

    // Leg 3
     vec3 color_leg3_1 = vec3(0.0, 0.65, 1.0) * leg3_1;
     vec3 color_leg3_2 = vec3(0.0, 0.55, 1.0) * leg3_2;
     vec3 color_leg3_3 = vec3(0.0, 0.45, 1.0) * leg3_3;
     vec3 color_leg3_4 = vec3(1.0, 0.1, 0.0) * leg3_4;
     vec3 color_leg3_5 = vec3(0.0, 0.45, 1.0) * leg3_5;
     vec3 color_leg3_6 = vec3(0.0, 0.45, 1.0) * leg3_6;


// Output to screen
      fragColor = vec4(
       color_head + color_head_ear1 + color_head_ear2 + color_head_nose +
       color_head_mouth1 + color_head_mouth2 + color_head_mouth3 +
       color_head_outer_eye - color_head_inner_eye + 

       color_neck1 + color_neck2 + color_neck3 + color_neck4 + 
       color_body1 + color_body2 + color_body3 +
       color_tail1 + color_tail2 + color_tail3 + color_tail4 +

       color_leg1_1 + color_leg1_2 + color_leg1_3 + color_leg1_4 + color_leg1_5 + color_leg1_6 +
       color_leg2_1 + color_leg2_2 + color_leg2_3 + color_leg2_4 + color_leg2_5 + color_leg2_6 +
       color_leg3_1 + color_leg3_2 + color_leg3_3 + color_leg3_4 + color_leg3_5 + color_leg3_6
       , 1.0);
}
