float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}

float circle( vec2 p, float s )
{
    float r = .01;
    return smoothstep(r, r - 0.005, abs(sdCircle(p, s)));
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}


float segment( in vec2 p, in vec2 a, in vec2 b )
{
    float r = .01;
    return smoothstep(r, r - 0.005, sdSegment(p, a, b));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 p = uv - 0.5;
    p.x *= iResolution.x / iResolution.y;

    // floor
    float d = segment(p, vec2(-1., -.3), vec2(1., -.3));
    // left leg
    d += segment(p, vec2(-.05, -.3), vec2(.0, -.1));
    // right leg
    d += segment(p, vec2(.05, -.3), vec2(.0, -.1));
    // spine
    d += segment(p, vec2(.0, -.1), vec2(.0, .1));
    // left arm
    d += segment(p, vec2(-.05, -.05), vec2(.0, .1));
    // right arm
    d += segment(p, vec2(.05, -.05), vec2(.0, .1));
    // neck
    d += segment(p, vec2(.0, .1), vec2(.0, .15));
    // head
    d += circle(p + vec2(.0, -.25), .1);


    // Output to screen
    fragColor = vec4(d);
}
