float digitalize(float raw, float power){
    return floor(mod(power*raw, power));
}

float combine(float theta, float rou, float power1, float power2){
    float v = digitalize(theta,power1) + 1e2*digitalize(rou,power2);
    float shape = sin(.0005*theta+v*.4)*1e3;


    return fract(shape);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
	vec2 p = -.5 + fragCoord.xy / iResolution.xy;
    float speed = .005;

    p.x *= iResolution.x/iResolution.y;

	float color = 1.5 - (1.5*length(4.*p));
    float color1 = color;


    float theta = atan(p.x,p.y);
    float rou = length(p)-1e1*iTime*speed;
    float power1 = 4.;float power2 = 64.;

    color += .33*combine(theta, rou, power1, power2);
    color += .33*combine(theta+1., rou+1., 2.*power1, 2.*power2);
    color += .33*combine(theta, rou, 5.*power1, 2.*power2);

	fragColor =vec4(color, pow(max(color,0.),2.)*0.4, pow(max(color,0.),3.)*0.15 , 1.0);
}
