void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy/iResolution.xy;

	vec4 col = texture(iChannel0, p);

    float t = iTime;

    //Play with this
    vec3 abColor = vec3(-0.0,1.5,3.0);

    abColor.r = 3.0*sin(t);
    abColor.g = 3.0*sin(t + 3.14/2.0);

    //Chromatic abberation
    vec2 offset = vec2(.01,.01);
    col.r = mix(col.r, texture(iChannel0, p+offset.xy).r,abColor.x);
    col.g = mix(col.g, texture(iChannel0, p+offset.xy).g,abColor.y);
    col.b = mix(col.b, texture(iChannel0, p+offset.xy).b,abColor.z);


    fragColor = col;
}
