void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    fragColor = texture(iChannel0,fragCoord/iResolution.xy);
}

#define brushSize 20.0
bool drawCircleMouse(out vec4 fragColor,vec2 fragCoord,vec4 mouse){
    // draw a circle if the mouse is clicked
    if(distance(mouse.xy, fragCoord) < brushSize && mouse.z > .0)
    {
        fragColor = vec4(1.0);
        return true;
    }
    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(drawCircleMouse(fragColor,fragCoord,iMouse)) return;
    fragColor = texture(iChannel0,fragCoord/iResolution.xy);
}
