const float scale = 6.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel1, vec2(iTime, 0)-uv*scale).rgb *
               texture(iChannel1, vec2(0, iTime)-uv*scale).rgb +
               texture(iChannel1, vec2(iTime, 0)+uv*scale).rgb *
               texture(iChannel1, vec2(0, iTime)+uv*scale).rgb;

    fragColor = vec4(col,1.0);
}
