float rand(vec2 coord, float rand1, float rand2, float rand3)
{
    return fract(sin(dot(coord, vec2(rand1, rand2))) * rand3);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 coord = uv * 10.0;

    float value = rand(coord, 12.9898 ,78.233,43758.5453);

    // Output to screen
    fragColor = vec4(vec3(value), 1.0);
}
