float polygon(in vec2 uv, float time)
{
    float PI = 3.1415926;
    float a = atan(uv.x, uv.y) + PI;
    float peices = 2.0  * PI / 4.0;
	float k = floor(0.6 + (a - time) / peices) * peices - a + time;


    float c = 1.0/atan((peices - 0.2) /k) * length(uv);

   // float c = k1;// k2 / length(uv);// cos(peices / 2.5) / cos(peices / 2.5 - k) * length(uv);
//
    if(c < 0.01 || c > 0.8){
        c = 0.0;
    }
    else
    {
        c = 0.5;
    }


    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 center =  4.* (  fragCoord - .5*iResolution.xy ) / iResolution.y;
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float c = polygon(center, iTime);

    fragColor = vec4(c ,0.0,0.0,1.0);
}
