void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    //vec3 colinv = vec3(1.0-col.x, 1.0-col.y, 1.0-col.z); 
    vec3 colinv = 1.-col;

    vec2 center = vec2(0.5, abs(cos(iTime)));

    float x = abs(uv.x - center.x);
    float y = abs(uv.y - center.y);
    float p = 0.3 * abs(cos(iTime*1.5));
    //col *= 0.5 + smoothstep(p-0.05, p+0.05, sqrt(x*x + y*y));
    if (step(p, sqrt(x*x + y*y)) > 0.0) {
        fragColor = vec4(col,1.0);
    } else {
        fragColor = vec4(colinv,1.0);
    }

}
