void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

#define a .004

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = fragCoord.xy/iResolution.xy;
    vec4 tc = iFrame < 7 ? texture(iChannel0, uv) : mix( texture(iChannel0, uv), texture(iChannel1, uv), .99);

    tc += texture(iChannel1, uv+vec2(-a,-a))*.5;
    tc += texture(iChannel1, uv+vec2(0.,-a))*.5;
    tc += texture(iChannel1, uv+vec2(a, -a))*.5;

    tc += texture(iChannel1, uv+vec2(-a,0.))*.5;
   // tc += texture(iChannel0, uv+vec2(0.,0.))*.5;
    tc += texture(iChannel1, uv+vec2(a, 0.))*.5;

    tc += texture(iChannel1, uv+vec2(-a, a))*.5;
    tc += texture(iChannel1, uv+vec2(0., a))*.5;
    tc += texture(iChannel1, uv+vec2(a,  a))*.5;

    tc *= .2;

    fragColor = tc;

}
