const float LINE_WIDTH = .01;

const vec2 EYE_R = vec2(.1,.1);
const vec2 EYE_L = vec2(-.1,.1);
const vec2 HAND_R = vec2(.5,0);
const vec2 HAND_L = vec2(-.5,0);
const vec2 MOUTH = vec2(0,-.1);

//palette 
const vec4 CAUC_H = vec4(0.9921875,0.93359375,0.87109375,1.0);
const vec4 CAUC_M = vec4(0.9921875,0.86328125,0.71484375,1.0);
const vec4 CAUC_S = vec4(0.91015625,0.79296875,0.65234375,1.0);
const vec4 BLACK = vec4(0.0,0.0,0.0,1.0);
const vec4 WHITE = vec4(1,1,1,1);
const vec4 RED = vec4(0.91015625,0.3359375,0.41015625,1.0);
const vec4 GREEN = RED.grba;
const vec4 BLUE = RED.bgra;

vec4 colorStep(vec4 ColorA,vec4 ColorB,float midpt,float x)
{
    float a = midpt - (LINE_WIDTH / 2.0);
    float b = a + LINE_WIDTH;

    vec4 toLine = smoothstep(midpt,a,x) * ColorA + smoothstep(a,midpt,x) * BLACK;
    vec4 fromLine = smoothstep(b,midpt,x) * BLACK + smoothstep(midpt,b,x) * ColorB;
    return toLine + fromLine;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Screen coords from -.5 to .5 0 with 0 in the middle using x scale as 1.0
    vec2 uv = (fragCoord- .5* iResolution.xy)/iResolution.xx;


    //gravity
    vec2 face = (3. +  2.0*mod(iTime, 4.0) + exp2(mod(iTime, 4.0))) * uv;

    // Output to screen
    //green to well
    fragColor = colorStep(WHITE / 1.7, GREEN, .3, length(uv));
    fragColor = colorStep(WHITE / 2.0, fragColor, .26, length(uv));
    fragColor = colorStep(BLACK, fragColor, .1667, length(uv));
    //timmy
    fragColor = colorStep(BLUE, fragColor, .6, length(vec2(face.x,face.y*2.0))); //shirt
    fragColor = colorStep(CAUC_M,fragColor, .3, length(face)); //face
    fragColor = colorStep(WHITE, fragColor, .3*.25, length(face-EYE_L)); //eyes
    fragColor = colorStep(WHITE, fragColor, .3*.25, length(face-EYE_R));
    fragColor = colorStep(BLUE, fragColor, .3*.25*.2667, length(face-EYE_L)); //pupils
    fragColor = colorStep(BLUE, fragColor, .3*.25*.2667, length(face-EYE_R));
    fragColor = colorStep(BLACK, fragColor, .3*.25, length(face-MOUTH)); //mouth
    fragColor = colorStep(CAUC_M, fragColor, .3*.25, length(face-HAND_L)); //hands
    fragColor = colorStep(CAUC_M, fragColor, .3*.25, length(face-HAND_R));
}
