void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 screenUV = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, screenUV) / float(iFrame + 1);
}

const float INF = 1e9;

const float GLASS_N = 1.5;
const float AIR_N = 1.0;

const float MARCH_SIZE = 0.01;

const float SPHERE_R = 1.0;
const float CYLINDER_R = 1.0;

const vec3 CAMERA_POS = vec3(10,2, -5);

const vec3 LIGHT_POS = vec3(2, 1, 2);
const float LIGHT_RADIUS = 0.25;
const vec4 LIGHT_COLOR = vec4(1, 0, 0, 1);

const vec3 LIGHT2_POS = vec3(-2, 1, -2);
const float LIGHT2_RADIUS = 0.25;
const vec4 LIGHT2_COLOR = vec4(0, 0.7, 0.8, 1);

const int EMISSION = 0;
const int DIFFUSE = 1;
const int REFLECTION = 2;
const int REFRACTION = 3;

vec3 randVals;
vec3 randDir;


float traceSphere(vec3 pos, vec3 dir, float R, out vec3 normal) 
{
    float b = dot(pos, dir);
    float D = b * b - (dot(pos, pos) - R * R);
    if (D < 0.0) {
        return INF;
    }
    float t = (-b - sqrt(D));
    if (t < 0.0) {
        t = (-b + sqrt(D));
    }
    if (t < 0.0) {
        return INF;
    }
    vec3 wPos = pos + t * dir;
    normal = normalize(wPos);
    return t;
}


float traceCylinder(vec3 pos, vec3 dir, inout vec3 normal) 
{
    float floorT = (-1.0 - pos.y) / dir.y;
    if (floorT < 0.0) {
        return INF;
    }
    vec3 finalPos = pos + dir * floorT;
    if (dot(finalPos.xz, finalPos.xz) < CYLINDER_R * CYLINDER_R) {
        normal = vec3(0, 1, 0);
        return floorT;
    }

    float b = dot(pos.xz, dir.xz);
    float a = dot(dir.xz, dir.xz);
    float D = b * b - (dot(pos.xz, pos.xz) - CYLINDER_R * CYLINDER_R) * a;
    if (D < 0.0) {
        return INF;
    }
    float t = (-b - sqrt(D)) / a;
    if (pos.y + t * dir.y < -1.0) {
        vec3 wPos = pos + t * dir;
        normal = normalize(vec3(wPos.x, 0, wPos.z));
        return t;
    }

    return INF;
}


float rand(float frame)
{
    return fract(sin( dot(vec3(frame), vec3(12.9898,78.233,45.5432) )) * 43758.5453);
}


float pow2(float x)
{
    return x * x;
}


float fogSdf(vec3 pos)
{
    return length(pos) - 0.75;
}


float BeerLambert(float absorptionCoefficient, float distanceTraveled)
{
    return exp(-absorptionCoefficient * distanceTraveled);
}


bool isOccluded(vec3 pos, vec3 target) 
{
    vec3 rvec = target - pos;
    float dist = length(rvec);
    vec3 dir = rvec / dist;

    vec3 normal = vec3(0, 0, 0);

    float t = traceSphere(pos, dir, SPHERE_R, normal);
    if (dist >= t) {
        return true;
    }
    vec3 cylNormal = normal;
    t = traceCylinder(pos, dir, cylNormal);
    return (t > 0.0 && dist >= t);
}


vec3 refraction(vec3 v_in, vec3 normal, float n1, float n2) 
{
    if (dot(v_in, normal) < 0.0) {
        normal = -normal;
    }
    float cosA = dot(v_in, normal);
    float sinA = sqrt(1.0 - cosA * cosA);
    vec3 tang = normalize(v_in - cosA * normal);
    float sinB = n1 * sinA / n2;
    float cosB = sqrt(1.0 - sinB * sinB);
    return tang * sinB + normal * cosB;
}


vec4 sampleFog(vec3 pos, vec3 dir)
{
    float t = fogSdf(pos);
    float opacity = 1.5;
    float absorption = 10.0;
    float totalAbs = 0.0;
    for (int i = 0; i < 512; ++i) {
        vec3 curPos = pos + (float(i) * MARCH_SIZE + t) * dir;
        float sdf = fogSdf(curPos);
        if (sdf < 0.0) {
            float prevOpacity = opacity;
            opacity *= BeerLambert(absorption * (
            texture(iChannel3, curPos * 0.2).x
            + texture(iChannel3, curPos * 0.4).x
            + texture(iChannel3, curPos * 0.8).x
            + texture(iChannel3, curPos * 1.6).x
            ) * 0.25 * abs(sdf), MARCH_SIZE);
            float absorptionLastStep = prevOpacity - opacity;
            totalAbs += absorptionLastStep;
        }
    }
    return vec4(vec3(0, 3, 0) * totalAbs, 1.0 - opacity);
}


vec3 computeLighting(vec3 worldPos, vec3 color, vec3 normal, vec3 viewVec) 
{
    vec3 lightPos = LIGHT_POS + randDir * LIGHT_RADIUS;
    vec3 toLight = lightPos - worldPos;
    bool occl = isOccluded(worldPos + normalize(toLight) * 1e-3, lightPos);
    float lightAtten = 20.0f / dot(toLight, toLight) * (occl ? 0.0 : 1.0);

    vec3 light2Pos = LIGHT2_POS + randDir * LIGHT2_RADIUS;
    vec3 toLight2 = light2Pos - worldPos;
    bool occl2 = isOccluded(worldPos + normalize(toLight2) * 1e-3, light2Pos);
    float light2Atten = 10.0f / dot(toLight2, toLight2) * (occl2 ? 0.0 : 1.0);

    return color * (
        max(0.0f, dot(normal, normalize(toLight))) * lightAtten * LIGHT_COLOR
        + max(0.0f, dot(normal, normalize(toLight2))) * light2Atten * LIGHT2_COLOR
        + textureLod(iChannel0, viewVec, 10.0) * 0.3
    ).rgb;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 screenUV = fragCoord/iResolution.xy;
    randVals = vec3(rand(float(iFrame)), rand(float(iFrame + 5)), rand(float(iFrame + 15)));
    randDir = normalize(randVals * 2.0 - 1.0);
    vec2 uv = (fragCoord - iResolution.xy * 0.5 + (randVals.xy - 0.5) * 2.0)/iResolution.x;

    vec3 currentPos = CAMERA_POS;

    vec3 forward = normalize(-currentPos);
    vec3 up = vec3(0, 1, 0);
    vec3 right = normalize(cross(forward, up));
    up = normalize(cross(right, forward));
    vec3 viewVec = normalize(forward + uv.x * right + uv.y * up);

    float curN = 1.0;
    float nextN = GLASS_N;
    float GLASS_R = pow2(AIR_N - GLASS_N) / pow2(AIR_N + GLASS_N);
    float GLASS_T = 1.0 - GLASS_R;

    int iterationType;
    vec3 color;
    float mult = 1.0;

    for (int i = 0; i < 4; i++) {
        vec3 normal = vec3(0, 0, 0);

        float t = traceSphere(currentPos - LIGHT_POS, viewVec, LIGHT_RADIUS, normal);
        if (t != INF) {
            color = LIGHT_COLOR.rgb * 5.0;
            iterationType = EMISSION;
        }

        float l2_t = traceSphere(currentPos - LIGHT2_POS, viewVec, LIGHT2_RADIUS, normal);
        if (t > l2_t) {
            t = l2_t;
            color = LIGHT2_COLOR.rgb;
            iterationType = EMISSION;
        }

        float sphere_t = traceSphere(currentPos, viewVec, SPHERE_R, normal);
        if (sphere_t < t) {
            if (GLASS_R > randVals.y) {
                iterationType = REFLECTION;
            } else {
                iterationType = REFRACTION;
            }
            t = sphere_t;
        }
        vec3 cylNormal = normal;
        float cylT = traceCylinder(currentPos, viewVec, cylNormal);
        if (cylT < t) {
            t = cylT;
            vec3 pos = currentPos + t * viewVec;
            color = texture(iChannel1, pos.xz * pos.y).rgb;
            normal = cylNormal;
            iterationType = DIFFUSE;
        }

        float floorT = (-1.2 - currentPos.y) / viewVec.y;
        if (floorT > 0.0 && floorT < t && length(currentPos + floorT * viewVec) < 10.0f) {
            t = floorT;
            vec3 pos = currentPos + t * viewVec;
            color = texture(iChannel2, pos.xz * 0.2).rgb;
            normal = vec3(0, 1, 0);
            iterationType = DIFFUSE;
        }
        if (t != INF) {
            if (iterationType == EMISSION) {
                fragColor.rgb = color;
                break;
            } else if (iterationType == DIFFUSE) {
                fragColor.rgb += mult * computeLighting(currentPos + t * viewVec, color, normal, viewVec);
                break;
            } else if (iterationType == REFLECTION) {
                currentPos += viewVec * t;
                fragColor.rgb += mult * computeLighting(currentPos + t * viewVec, color, normal, viewVec);
                viewVec = reflect(viewVec, normal);
                currentPos += viewVec * 1e-5;
            } else if (iterationType == REFRACTION) {
                if (float(iTime) > 1.0) { 
                currentPos += viewVec * t;
                viewVec = refraction(viewVec, normal, curN, nextN);
                currentPos += viewVec * 1e-5;
                vec4 fog = sampleFog(currentPos, viewVec);                            
                fragColor.rgb += (fog.rgb * fog.a * mult) / 2.0;                
                mult *= 1.0 - fog.a;
                float tmp = curN;
                curN = nextN;
                nextN = tmp;
                }
            }
        } else {
            fragColor +=  texture(iChannel0, viewVec) * 0.3;
            break;
        }
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 screenUV = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, screenUV) + texture(iChannel1, screenUV);
}
