// "Circuit Diagram2" by ntsutae (modified by ikertesz)
// https://twitter.com/ntsutae/status/1268820823952916486
// https://www.openprocessing.org/sketch/912094
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  int x = int(fragCoord.x - 10.0 * iTime);
  int y = int(fragCoord.y + 200.0 * iTime);
  int r = (x-y)^(x+y)*1;
  bool b = abs(r*r/(y+int(iTime*35.0))+(x-int(iTime*70.0))) % (350) < 30;
  fragColor = vec4(vec3(b ? 1.0 : 0.0), 1.0);
}
