void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float radius = 1.;
    float warpAmount = 100.;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 newCoord = vec2(fragCoord.x, fragCoord.y);
    vec2 difference = iMouse.xy - newCoord.xy;
    if((abs(difference.x/iResolution.x) < radius) && (abs(difference.y/iResolution.y) < radius) ) {
        newCoord.x += ((((difference.x)/iResolution.x)/radius)*warpAmount);
        newCoord.y += ((((difference.y)/iResolution.y)/radius)*warpAmount);
    }
    vec2 uv = ((newCoord)/iResolution.xy);

    float amount = 1.1;
    vec4 video = texture(iChannel0, uv);

    // Output to screen
    fragColor = video;
}
