mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

mat2 scale(vec2 _scale){
    return mat2(_scale.x,0.0,
                0.0,_scale.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/min(iResolution.x, iResolution.y);
    uv=uv*rotate2d(iTime);
    uv=uv*scale(vec2(sin(iTime/4.0)/1.5));
    uv+=sin(iTime)/4.0+1.0;

    uv = fract(uv*4.)-0.5;
    vec2 uvf = floor((uv/4.0)+0.5);

    float d=length(uv);
    float alpha = atan(uv.x, uv.y)-(iTime*2.)+d*10.*sin(iTime);

    float x=(alpha/2.0/3.1415926535+0.5)*5.0;
    float m=min(fract(x), fract(1.0-x));


    vec3 color = texture(iChannel0, vec2(m/8.0+iTime/20.0, 0.0)).xyz*1.3;    
    if (uv.x>.48 || uv.y>.48) {
        color = vec3(1);
    }

	fragColor = vec4(color, 1.0);
}
