void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv *= 6.5;

    float len;
    for(int i = 0; i < 5; i++) {
        len = length(uv) / 2.;
        uv.x +=  sin(uv.y + iTime * 0.5);
        uv.y +=  cos(uv.x + iTime * 0.5 + cos(len * 4.0))*1.;
    }

    vec3 col = vec3(cos(len + 0.7), cos(len + 0.1), cos(len - 0.1));

    fragColor = vec4(col,1.0);
}
