//Trans rights are human rights
const float PHI = 1.61803398874989484820459;
//change this factor [0.0 to 1.0] to change the amount of dots in each frame
const float factor = 0.5;

float gold_noise(in vec2 xy, in float seed) {
    return fract(tan(distance(xy*PHI,xy)*seed)*xy.x);
}

void mainImage( out vec4 fragColor, in vec2 uv ) {
    float a = gold_noise(uv * 64.0, fract(iTime));
    float b = 1.0 - (mod(uv.x * a, 16.0) * mod(uv.y * a, 16.0));
    float o = floor(a * b / factor);
    fragColor = vec4(o, o, o, 1.0);
}
