#define T texture(iChannel0,(a=ceil(p)/3e2).xz)
void mainImage(out vec4 O, vec2 I)
{
    vec3 r = iResolution,
    a = r,
    p = sin(a+iTime),
    d = a;

    for(d.xy = (a.xy-roundEven(I)*2.)/a.x;
        T.g>abs(a.y+a*a).x/.2+.2 && a.z<1.;
        p += d*min(a = fract(-abs(p))/abs(d)+3e-4, min(a.y,a.z)).x);
    O = T*2e2/r.x/length(fwidth(p))+p.z/3e2; O*=O;
}
