//Checkerboard by makslane
//Derived from: https://www.shadertoy.com/view/XdfyWf

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    int on = ((int(fragCoord.x/100.0) % 2 == 0) && (int(fragCoord.y/100.0) % 2 == 0)) || ((int(fragCoord.x/100.0) % 2 != 0) && (int(fragCoord.y/100.0) % 2 != 0))?1:0;


    vec3 col = vec3(on,on,on);

    // Output to screen
    fragColor = vec4(col,1.0);
}
