#define rot(a) mat2( cos(a), -sin(a),sin(a), cos(a) )

float circle(float radius, vec2 position, vec2 uv){
    float d = distance(uv, position)/radius;
    float mask = clamp(floor(d), 0., 1.);
    return mask;
}

float halfScreen(vec2 uv){
    return clamp(ceil(max(0., uv.x)), 0., 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord- .5*iResolution.xy ) / iResolution.y;

    float x = uv.x;
    float y = uv.y;

    vec2 p = rot(-iTime)*uv*(sin(iTime*0.5)*0.2+1.1);

    float m = 1.-min(1.-circle(0.41, vec2(0.), p), min(circle(0.05, vec2(0.,0.2), p), max(1.-circle(0.05, vec2(0.,-0.2), p), min(circle(0.2, vec2(0.,-0.2), p), max(1.-halfScreen(p), max(1.-circle(0.2, vec2(0.,0.2), p), circle(0.4, vec2(0.), p)))))));

    vec3 col = vec3(m);

    fragColor = vec4(col,1.0);
}
