void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy; // vector (u,v)  : range 0<=u<=1, 0<=v<=1   (0,0): left bottom corner
    uv -=.5;//centerize
    uv.x *= iResolution.x/iResolution.y;// neutrize the influence by aspect ratio // uv.y size is maintained

    //animation by mouse move + time
    //float m=iMouse.x/iResolution.x +sin(iTime*.5)*.5+.5;
    float m=sin(iTime*.1)*.5+.5;
    float zoom =pow(10., -m*5.);

    vec2 target=vec2(-1.40196, .0);
    //vec2 target=vec2(.25005, .0);

    ///////////////////////////////// core of mandelbrot concept 
    vec2 z=vec2(0,0);// initial z value
    vec2 c=uv*zoom*3. +target;// offset vector c after squared
    float score=0.;// score of the dance step
    const float limit = 20000.;// resolution of the image
    while (length(z)<2. && score<limit){
        z=vec2(z.x*z.x - z.y*z.y, 2.*z.x*z.y)+c;// z = z*z + c
        if(length(z)>2.)break;// if z is bigger than 2, it never stays in the target area 
        score++;
    }    
    float f =score/limit;// how much the dot could stay in the range 
    f=pow(f,.35);

    //vec3 col =vec3(f);
    vec2 texuv=vec2(f,.5);//uv to get color from texture
    vec3 col = texture (iChannel0, texuv).rgb;
    fragColor = vec4(col,1.0);
}
