float smoothmin(float d1, float d2, float k){
    float h = exp(-k * d1) + exp(-k * d2);
    return -log(h) / k;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 r = 2.0 * vec2(uv.x - 0.5, uv.y - 0.5);
    r.x *= iResolution.x / iResolution.y;
    vec3 col = vec3(0.767,0.618,0.427);

    r.x /= 0.5;
    float warp = smoothmin(0.0,( (r.x - 1.2)*(r.x - 1.2) + (r.y-0.1)*(r.y-0.1) - 0.4 ) * 2.0, 8.0);
    r -= vec2(2.0,-0.5);

    float distance = sqrt(r.x*r.x+r.y*r.y +0.96 + warp + sin((atan(r.y/r.x)+0.1)*10. )* 0.02*(r.x*r.x + r.y*r.y));
    float nyanko = mod(distance,0.2);
    if(nyanko<=0.05){
        col = vec3(0.6,0.419,0.246);
    }

    fragColor = vec4(col,1.0);
}
