//useful functions
vec2 rotate(vec2 v, float a) { return mat2(cos(a), -sin(a), sin(a), cos(a))*v; }
float remap(float v, float a, float b, float c, float d) { return (v - a) / (b - a) * (d - c) + c; }
float divMod(float a, float m) { return remap(mod(a, m), 0.0, m, -1.0, 1.0); }

void mainImage( out vec4 p, in vec2 fragCoord )
{
	vec2 uv = gl_FragCoord.xy / iResolution.xy;
  uv -= 0.5;
  uv.x = divMod(uv.x, exp(1. / iTime));
  uv.y = divMod(uv.y, 2.);

  for (int i = 0; i < 10; ++i){
    uv = abs(uv) - 0.09;
    uv = rotate(uv, 0.5*iTime);
    uv = rotate(uv, abs(uv.y));
    uv = rotate(uv, abs(uv.x));
  }
  for (float i = 0.; i < atan(iTime); i+=0.5) {
    uv = rotate(uv, exp(abs(uv.y))- 0.2);
    uv = rotate(uv, atan(i));
  }
  float r = 0.2+uv.x/uv.y;
  vec3 q = cross(vec3(uv.x, uv.y, uv.x*uv.y), vec3(2, 0.5, 0.5)); 
  p = vec4(q-r, 1);
}
