float rect (vec2 uv, vec4 r, float blur) {
    float left = smoothstep(r.x - blur, r.x, uv.x);
    float right = smoothstep(r.z, r.z + blur, uv.x);
    float top = smoothstep(r.y - blur, r.y, 1. - uv.y);
    float bottom = smoothstep(r.w, r.w + blur, 1. - uv.y);
    return (top - bottom) * (left - right);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float cursor = texture(iChannel0, uv).r;
    float box = rect(uv, vec4(0.25, 0.25, 0.75, 0.75), .03);
    box -= cursor;
    box = smoothstep(0.3, 0.3, box);
    vec3 tex = texture(iChannel1, uv).rgb;
    vec3 col = tex * box;

    col = mix(vec3(.9, .9, uv.y * .1 + .8 ), col, box); 

    fragColor = vec4(col,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec2 m = (iMouse.xy-0.5*iResolution.xy)/iResolution.y;
    float c = 1. - smoothstep(0., 0.2, length(uv - m) * 1.3);
    vec3 col = vec3(c);
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb * 0.9 + texture(iChannel1, uv).rgb * 0.96;
    fragColor = vec4(col,.9);
}
