vec3 sphere(in vec3 col,in vec4 s,in vec3 albedo){
	col+=albedo*(s.w/dot(s.xyz,s.xyz));
	return col;

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy-0.5)*10.0;
    vec3 pos=vec3(uv,1.0);
    vec3 col=vec3(0.1);
	for(int i=0;i<100;i++){
		float f=float(i);
		col=sphere(col,vec4(pos+vec3(cos(iTime+0.3+f*1.4),sin(iTime+f*1.3),cos(iTime+f*1.2)),sin(iTime*1.2+f*10.2)),vec3(0,1,0));
	}
    fragColor.rgb=col;
}
